.gradient-card {
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.25) 0%, rgba(255, 255, 255, 0.1) 100%);
  border: 1px solid rgba(255, 255, 255, 0.3);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);
}

.dark .gradient-card {
  background: linear-gradient(135deg, rgba(15, 23, 42, 0.4) 0%, rgba(30, 41, 59, 0.3) 100%);
  border: 1px solid rgba(255, 255, 255, 0.15);
}

/* Clean enhanced glass effect */
.glassmorphism-enhanced {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
  border-radius: 12px;
}

.dark .glassmorphism-enhanced {
  background: rgba(15, 23, 42, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track {
  background: hsl(var(--muted));
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--border));
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted-foreground));
}

/* Focus styles */
.focus-visible {
  @apply outline-none ring-2 ring-ring ring-offset-2 ring-offset-background;
}

/* Form input enhancements */
input[type="number"]::-webkit-outer-spin-button,
input[type="number"]::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}

input[type="number"] {
  -moz-appearance: textfield;
}

/* Card hover effects */
.card-hover {
  @apply transition-all duration-200 hover:shadow-lg hover:scale-[1.02];
}

/* Button variations */
.button-gradient {
  background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--primary) / 0.8) 100%);
  @apply text-primary-foreground hover:opacity-90 transition-opacity;
}

/* Status indicators */
.status-indicator {
  @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium;
}

.status-pending {
  @apply bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200;
}

.status-processing {
  @apply bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200;
}

.status-completed {
  @apply bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200;
}

.status-failed {
  @apply bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200;
}

/* Loading animations */
.loading-spinner {
  @apply animate-spin rounded-full border-2 border-primary border-t-transparent;
}

/* Responsive utilities */
@media (max-width: 768px) {
  .glassmorphism {
    backdrop-filter: blur(8px);
    -webkit-backdrop-filter: blur(8px);
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .glassmorphism {
    background: hsl(var(--card));
    border: 2px solid hsl(var(--border));
    backdrop-filter: none;
    -webkit-backdrop-filter: none;
  }
}

/* Falling Stars Effect */
@keyframes fallingStar {
  0% {
    transform: translate3d(0, -100vh, 0) rotate(0deg);
    opacity: 0;
  }
  10% {
    opacity: 1;
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translate3d(0, 100vh, 0) rotate(360deg);
    opacity: 0;
  }
}

@keyframes twinkle {
  0%, 100% {
    opacity: 0.2;
  }
  50% {
    opacity: 0.8;
  }
}

.falling-stars-container {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 1;
  overflow: hidden;
}

.falling-star {
  position: absolute;
  width: 2px;
  height: 2px;
  background: radial-gradient(circle, rgba(255, 255, 255, 0.8) 0%, rgba(255, 255, 255, 0.4) 50%, transparent 100%);
  border-radius: 50%;
  animation: fallingStar linear infinite;
  opacity: 0;
  will-change: transform, opacity;
}

.falling-star::before {
  content: '';
  position: absolute;
  top: -1px;
  left: -1px;
  width: 4px;
  height: 4px;
  background: radial-gradient(circle, rgba(138, 43, 226, 0.3) 0%, rgba(75, 0, 130, 0.2) 50%, transparent 100%);
  border-radius: 50%;
  animation: twinkle 2s ease-in-out infinite;
}

.dark .falling-star {
  background: radial-gradient(circle, rgba(255, 255, 255, 0.6) 0%, rgba(255, 255, 255, 0.3) 50%, transparent 100%);
}

.dark .falling-star::before {
  background: radial-gradient(circle, rgba(147, 51, 234, 0.4) 0%, rgba(168, 85, 247, 0.3) 50%, transparent 100%);
}

/* Individual star variations for natural movement */
.falling-star:nth-child(1) {
  left: 10%;
  animation-duration: 8s;
  animation-delay: -2s;
}

.falling-star:nth-child(2) {
  left: 20%;
  animation-duration: 12s;
  animation-delay: -5s;
}

.falling-star:nth-child(3) {
  left: 35%;
  animation-duration: 10s;
  animation-delay: -1s;
}

.falling-star:nth-child(4) {
  left: 50%;
  animation-duration: 15s;
  animation-delay: -8s;
}

.falling-star:nth-child(5) {
  left: 70%;
  animation-duration: 9s;
  animation-delay: -3s;
}

.falling-star:nth-child(6) {
  left: 80%;
  animation-duration: 13s;
  animation-delay: -6s;
}

.falling-star:nth-child(7) {
  left: 90%;
  animation-duration: 11s;
  animation-delay: -4s;
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .falling-star {
    width: 1.5px;
    height: 1.5px;
  }

  .falling-star::before {
    width: 3px;
    height: 3px;
  }

  /* Reduce number of stars on mobile */
  .falling-star:nth-child(n+5) {
    display: none;
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  .animate-fade-in,
  .animate-slide-up,
  .animate-pulse-slow,
  .falling-star {
    animation: none;
  }

  .falling-stars-container {
    display: none;
  }

  * {
    transition-duration: 0.01ms !important;
    animation-duration: 0.01ms !important;
  }
}

