import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { 
  Settings, 
  Wallet, 
  Key,
  AlertCircle,
  Save,
  BarChart3,
  DollarSign,
  TrendingUp,
  Activity
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { AdminLayout } from "@/components/admin-layout";

interface AdminUser {
  id: string;
  email: string;
  role: string;
}

interface WalletSetting {
  id: string;
  currency: string;
  address: string;
  network: string;
  isActive: boolean;
  createdAt: string;
}

interface PlatformSetting {
  id: string;
  key: string;
  value: string;
  description: string | null;
  isEncrypted: boolean;
  updatedAt: string;
}

export function AdminPanelPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [user, setUser] = useState<AdminUser | null>(null);
  const [loading, setLoading] = useState(true);
  const [walletSettings, setWalletSettings] = useState<WalletSetting[]>([]);
  const [platformSettings, setPlatformSettings] = useState<PlatformSetting[]>([]);

  useEffect(() => {
    checkAuth();
    loadData();
  }, []);

  const checkAuth = async () => {
    const token = localStorage.getItem('auth_token');
    if (!token) {
      setLocation('/admin/login');
      return;
    }

    try {
      const response = await fetch('/api/auth/verify', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const result = await response.json();
        if (result.user.role !== 'admin') {
          toast({
            title: "Access Denied",
            description: "You don't have admin privileges",
            variant: "destructive",
          });
          setLocation('/');
          return;
        }
        setUser(result.user);
      } else {
        localStorage.removeItem('auth_token');
        setLocation('/admin/login');
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('auth_token');
      setLocation('/admin/login');
    } finally {
      setLoading(false);
    }
  };

  const loadData = async () => {
    const token = localStorage.getItem('auth_token');
    if (!token) return;

    try {
      // Load wallet settings
      const walletResponse = await fetch('/api/admin/wallets', {
        headers: { 'Authorization': `Bearer ${token}` },
      });
      if (walletResponse.ok) {
        const wallets = await walletResponse.json();
        setWalletSettings(wallets);
      }

      // Load platform settings
      const settingsResponse = await fetch('/api/admin/settings', {
        headers: { 'Authorization': `Bearer ${token}` },
      });
      if (settingsResponse.ok) {
        const settings = await settingsResponse.json();
        setPlatformSettings(settings);
      }
    } catch (error) {
      console.error('Failed to load data:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('auth_token');
    toast({
      title: "Logged out",
      description: "You have been successfully logged out",
    });
    setLocation('/admin/login');
  };

  const saveWalletSetting = async (currency: string, address: string, network: string) => {
    const token = localStorage.getItem('auth_token');
    if (!token) return;

    try {
      const response = await fetch('/api/admin/wallets', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ currency, address, network, isActive: true }),
      });

      if (response.ok) {
