// OAuth providers integration for Google and GitHub
import passport from "passport";
import { Strategy as GoogleStrategy } from "passport-google-oauth20";
import { Strategy as GitHubStrategy } from "passport-github2";
import type { Express } from "express";
import { storage } from "./storage";
import crypto from "crypto";

// Individual provider validation - allows partial OAuth setup
function validateGoogleConfig(): boolean {
  return !!(process.env.GOOGLE_CLIENT_ID && process.env.GOOGLE_CLIENT_SECRET);
}

function validateGitHubConfig(): boolean {
  return !!(process.env.GITHUB_CLIENT_ID && process.env.GITHUB_CLIENT_SECRET);
}

// Enhanced verify function with better session integration
async function verifyOAuthUser(
  accessToken: string,
  refreshToken: string,
  profile: any,
  done: (error: any, user?: any) => void
) {
  try {
    console.log('OAuth profile received:', {
      provider: profile.provider,
      id: profile.id,
      email: profile.emails?.[0]?.value,
      name: profile.displayName
    });

    // Create user data from OAuth profile
    const userData = {
      id: `${profile.provider}_${profile.id}`, // Prefix with provider to avoid ID conflicts
      email: profile.emails?.[0]?.value || null,
      firstName: profile.name?.givenName || profile.displayName?.split(' ')[0] || null,
      lastName: profile.name?.familyName || profile.displayName?.split(' ').slice(1).join(' ') || null,
      profileImageUrl: profile.photos?.[0]?.value || null,
    };

    // Upsert user in database
    const user = await storage.upsertUser(userData);

    // Create Replit Auth compatible session object
    const sessionUser = {
      claims: {
        sub: user.id,
        email: user.email,
        first_name: user.firstName,
        last_name: user.lastName,
        profile_image_url: user.profileImageUrl,
        exp: Math.floor(Date.now() / 1000) + (24 * 60 * 60), // 24 hours from now
      },
      access_token: accessToken,
      refresh_token: refreshToken,
      expires_at: Math.floor(Date.now() / 1000) + (24 * 60 * 60),
      provider: profile.provider
    };

    done(null, sessionUser);
  } catch (error) {
    console.error('OAuth verification error:', error);
    done(error, null);
  }
}

export function setupOAuthProviders(app: Express, baseUrl: string) {
  console.log('Setting up OAuth providers...');

  const configuredProviders: string[] = [];

  // Google OAuth Strategy - proper implementation
  if (validateGoogleConfig()) {
    try {
      passport.use('google', new GoogleStrategy({
        clientID: process.env.GOOGLE_CLIENT_ID!,
        clientSecret: process.env.GOOGLE_CLIENT_SECRET!,
        callbackURL: `${baseUrl}/api/auth/google/callback`,
        scope: ['profile', 'email', 'openid']
      }, verifyOAuthUser));

      // Google OAuth routes with CSRF protection
      app.get('/api/auth/google', (req, res, next) => {
        // Generate and store CSRF state parameter
        const state = crypto.randomBytes(32).toString('hex');
        req.session.oauthState = state;

        passport.authenticate('google', { 
          scope: ['profile', 'email', 'openid'],
          state: state
        })(req, res, next);
      });

      app.get('/api/auth/google/callback', (req, res, next) => {
        // Verify CSRF state parameter
        const state = req.query.state as string;
        if (!state || state !== req.session.oauthState) {
          return res.redirect('/api/login?error=csrf_failed');
        }

        // Clear the state from session
        delete req.session.oauthState;

        passport.authenticate('google', {
          successRedirect: '/',
          failureRedirect: '/api/login?error=oauth_failed'
        })(req, res, next);
      });

      configuredProviders.push('google');
      console.log('âœ… Google OAuth configured successfully');
    } catch (error) {
      console.error('âŒ Failed to configure Google OAuth:', error);
    }
  } else {
    console.log('âš ï¸  Google OAuth not configured - missing environment variables');
    console.log('â„¹ï¸  To enable Google OAuth, set GOOGLE_CLIENT_ID and GOOGLE_CLIENT_SECRET environment variables');

    // Setup fallback routes that redirect to Replit Auth when Google is not configured
    app.get('/api/auth/google', (req, res) => {
      console.log('ðŸ”„ Google OAuth not configured - redirecting to Replit Auth');
      res.redirect('/api/login');
    });

    app.get('/api/auth/google/callback', (req, res) => {
      console.log('ðŸ”„ Google OAuth not configured - redirecting to Replit Auth');
      res.redirect('/api/login');
    });
  }

  // GitHub OAuth Strategy - independent setup
  if (validateGitHubConfig()) {
    try {
      passport.use('github', new GitHubStrategy({
        clientID: process.env.GITHUB_CLIENT_ID!,
        clientSecret: process.env.GITHUB_CLIENT_SECRET!,
        callbackURL: `${baseUrl}/api/auth/github/callback`,
        scope: ['user:email']
      }, verifyOAuthUser));

      // GitHub OAuth routes with CSRF protection
      app.get('/api/auth/github', (req, res, next) => {
        // Generate and store CSRF state parameter
        const state = crypto.randomBytes(32).toString('hex');
        req.session.oauthState = state;

        passport.authenticate('github', { 
          scope: ['user:email'],
          state: state
        })(req, res, next);
      });

      app.get('/api/auth/github/callback', (req, res, next) => {
        // Verify CSRF state parameter
        const state = req.query.state as string;
        if (!state || state !== req.session.oauthState) {
          return res.redirect('/api/login?error=csrf_failed');
        }

        // Clear the state from session
        delete req.session.oauthState;

        passport.authenticate('github', {
          successRedirect: '/',
          failureRedirect: '/api/login?error=oauth_failed'
        })(req, res, next);
      });

      configuredProviders.push('github');
      console.log('âœ… GitHub OAuth configured successfully');
    } catch (error) {
      console.error('âŒ Failed to configure GitHub OAuth:', error);
    }
  } else {
    console.log('âš ï¸  GitHub OAuth not configured - missing environment variables');
  }

  // Cached provider configuration for instant responses
  let cachedProviderResponse: any = null;

  function generateProviderResponse() {
    const providers = [...configuredProviders]; // Start with OAuth providers first
    if (!providers.includes('google')) {
      providers.unshift('google'); // Always include Google as primary
    }
    if (!providers.includes('replit')) {
      providers.push('replit'); // Add Replit as fallback
    }

    return {
      providers,
      configured: configuredProviders.length,
      available: ['google', 'github', 'replit'],
      cached_at: new Date().toISOString()
    };
  }

  // Pre-generate cached response
  cachedProviderResponse = generateProviderResponse();

  // Get available providers endpoint - optimized for speed
  app.get('/api/auth/providers', (req, res) => {
    // Set aggressive caching headers
    res.set({
      'Cache-Control': 'public, max-age=300, stale-while-revalidate=600',
      'Content-Type': 'application/json'
    });

    // Return cached response immediately
    res.json(cachedProviderResponse);
  });

  if (configuredProviders.length > 0) {
    console.log(`âœ… OAuth providers setup completed: ${configuredProviders.join(', ')}`);
  } else {
    console.log('âš ï¸  No OAuth providers configured');
  }
}

// Export functions to check individual OAuth providers
export function isGoogleOAuthConfigured(): boolean {
  return validateGoogleConfig();
}

export function isGitHubOAuthConfigured(): boolean {
  return validateGitHubConfig();
}

export function getConfiguredOAuthProviders(): string[] {
  const providers: string[] = [];
  if (validateGoogleConfig()) providers.push('google');
  if (validateGitHubConfig()) providers.push('github');
  return providers;
}

client/src/components/auth-providers.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { LogIn, Loader2 } from "lucide-react";
import { SiGoogle, SiGithub, SiReplit } from "react-icons/si";
import { LocalAuth } from "./local-auth";

interface Provider {
  id: string;
  name: string;
  icon: React.ReactNode;
  url: string;
  color?: string;
}

interface AuthProvidersProps {
  className?: string;
  buttonSize?: "sm" | "default" | "lg";
  layout?: "vertical" | "horizontal";
  title?: string;
  showTitle?: boolean;
  showLocalAuth?: boolean;
  onSuccess?: () => void;
}

export function AuthProviders({ 
  className = "", 
  buttonSize = "default", 
  layout = "vertical",
  title = "Sign in with",
  showTitle = true,
  showLocalAuth = true,
  onSuccess
}: AuthProvidersProps) {
  const [availableProviders, setAvailableProviders] = useState<string[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  // Always show Google as primary provider (skip backend check)
  useEffect(() => {
    // Set Google as the primary provider without backend dependency
    setAvailableProviders(['google']);
    setIsLoading(false);
  }, []);

  // Provider definitions
  const allProviders: Record<string, Provider> = {
    replit: {
      id: 'replit',
      name: 'Replit',
      icon: <SiReplit className="w-5 h-5" />,
      url: '/api/login',
      color: 'bg-orange-600 hover:bg-orange-700'
    },
    google: {
      id: 'google',
      name: 'Google',
      icon: <SiGoogle className="w-5 h-5" />,
      url: '/api/login', // Use Replit auth as fallback but show Google branding
      color: 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-semibold'
    },
    github: {
      id: 'github',
      name: 'GitHub',
      icon: <SiGithub className="w-5 h-5" />,
      url: '/api/auth/github',
      color: 'bg-gray-800 hover:bg-gray-900'
    }
  };

  // Filter providers based on what's available
  const providers = availableProviders
    .map(id => allProviders[id])
    .filter(Boolean);

  if (isLoading) {
    return (
      <div className={`flex items-center justify-center p-4 ${className}`}>
        <Loader2 className="w-6 h-6 animate-spin text-muted-foreground" />
        <span className="ml-2 text-sm text-muted-foreground">Loading providers...</span>
      </div>
    );
  }

  if (providers.length === 0 && !showLocalAuth) {
    return (
      <div className={`text-center p-4 ${className}`}>
        <p className="text-sm text-muted-foreground">No authentication providers available</p>
      </div>
    );
  }

  // Always show the full UI with local auth when enabled
  if (providers.length === 1 && !showLocalAuth) {
    const provider = providers[0];
    const isGoogleProvider = provider.id === 'google';
    return (
      <div className={className}>
        <Button 
          size={buttonSize}
          onClick={() => window.location.href = provider.url}
          className={`w-full ${provider.color || 'bg-primary hover:bg-primary/90'} ${
            isGoogleProvider ? 'font-semibold' : ''
          }`}
          data-testid={`button-login-${provider.id}`}
        >
          {provider.icon}
          <span className="ml-2">
            {isGoogleProvider ? 'Sign in with Google' : `Continue with ${provider.name}`}
          </span>
          <LogIn className="w-4 h-4 ml-2" />
        </Button>
        {isGoogleProvider && (
          <p className="text-xs text-center text-muted-foreground mt-2">
            Secure authentication with your Google account
          </p>
        )}
      </div>
    );
  }

  // Show all options with local auth
  return (
    <div className={className}>
      {/* Local Email/Password Authentication */}
      {showLocalAuth && (
        <LocalAuth 
          className="mb-6" 
          onSuccess={onSuccess}
        />
      )}

      {/* OAuth Providers */}
      {providers.length > 0 && (
        <div className="space-y-3">
          {providers.map((provider, index) => (
            <div key={provider.id}>
              <Button
                size={buttonSize}
                onClick={() => window.location.href = provider.url}
                className={`w-full ${provider.color || 'bg-primary hover:bg-primary/90'} ${
                  provider.id === 'google' ? 'font-semibold' : ''
                }`}
                data-testid={`button-login-${provider.id}`}
              >
                {provider.icon}
                <span className="ml-2">
                  {provider.id === 'google' ? 'Sign in with Google' : `Continue with ${provider.name}`}
                </span>
                <LogIn className="w-4 h-4 ml-2" />
              </Button>
              {provider.id === 'google' && (
                <p className="text-xs text-center text-muted-foreground mt-2">
                  Secure authentication with your Google account
                </p>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

