  change, 
  icon: Icon, 
  color, 
  isLoading = false, 
  testId 
}: KPICardProps) {
  const colors = colorClasses[color];

  if (isLoading) {
    return (
      <Card className="bg-white dark:bg-gray-800" data-testid={`${testId}-loading`}>
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div className="space-y-2">
              <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
              <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
            </div>
            <div className={`h-12 w-12 ${colors.bg} rounded-lg flex items-center justify-center`}>
              <Icon className={`h-6 w-6 ${colors.icon}`} />
            </div>
          </div>
          <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded mt-2 animate-pulse"></div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-white dark:bg-gray-800 transition-all duration-200 hover:shadow-lg" data-testid={testId}>
      <CardContent className="p-6">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-600 dark:text-gray-400" data-testid={`${testId}-title`}>
              {title}
            </p>
            <p className="text-2xl font-bold text-gray-900 dark:text-white mt-1" data-testid={`${testId}-value`}>
              {typeof value === 'number' ? value.toLocaleString() : value}
            </p>
          </div>
          <div className={`h-12 w-12 ${colors.bg} rounded-lg flex items-center justify-center`}>
            <Icon className={`h-6 w-6 ${colors.icon}`} />
          </div>
        </div>
        {change && (
          <p className={`text-xs mt-2 ${change.value >= 0 ? colors.change : 'text-red-600 dark:text-red-400'}`} data-testid={`${testId}-change`}>
            {change.value >= 0 ? '+' : ''}{change.value}% {change.label}
          </p>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/VolumeChart.tsx.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { TrendingUp } from "lucide-react";

interface VolumeData {
  date: string;
  volume: number;
  orders: number;
}

interface VolumeChartProps {
  data: VolumeData[];
  isLoading?: boolean;
  className?: string;
}


export function VolumeChart({ data = [], isLoading = false, className }: VolumeChartProps) {
  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(value);
  };

  const formatDate = (date: string) => {
    return new Date(date).toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric' 
    });
  };

  if (isLoading) {
    return (
      <Card className={`bg-white dark:bg-gray-800 ${className}`}>
        <CardHeader>
          <div className="flex items-center space-x-2">
            <div className="h-5 w-5 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
            <div className="h-6 bg-gray-200 dark:bg-gray-700 rounded w-32 animate-pulse"></div>
          </div>
          <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-48 animate-pulse"></div>
        </CardHeader>
        <CardContent>
          <div className="h-80 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
        </CardContent>
      </Card>
    );
  }

  // Calculate trend from real data
  const latestVolume = data && data.length > 0 ? data[data.length - 1]?.volume || 0 : 0;
  const previousVolume = data && data.length > 1 ? data[data.length - 2]?.volume || 0 : 0;
  const trend = previousVolume > 0 ? ((latestVolume - previousVolume) / previousVolume) * 100 : 0;

  // Handle empty data state
  if (!data || data.length === 0) {
    return (
      <Card className={`bg-white dark:bg-gray-800 ${className}`} data-testid="volume-chart">
        <CardHeader>
          <CardTitle className="flex items-center text-gray-900 dark:text-white">
            <TrendingUp className="h-5 w-5 mr-2 text-purple-600 dark:text-purple-400" />
            Transaction Volume (7 Days)
          </CardTitle>
          <CardDescription className="text-gray-600 dark:text-gray-400">
            No volume data available
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="h-80 flex items-center justify-center text-gray-500 dark:text-gray-400">
            No transaction data to display
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={`bg-white dark:bg-gray-800 ${className}`} data-testid="volume-chart">
      <CardHeader>
        <CardTitle className="flex items-center text-gray-900 dark:text-white">
          <TrendingUp className="h-5 w-5 mr-2 text-purple-600 dark:text-purple-400" />
          Transaction Volume (7 Days)
        </CardTitle>
        <CardDescription className="text-gray-600 dark:text-gray-400">
          Daily transaction volume and order counts
          {trend !== 0 && (
            <span className={`ml-2 text-xs ${trend >= 0 ? 'text-green-600 dark:text-green-400' : 'text-red-600 dark:text-red-400'}`}>
              {trend >= 0 ? '+' : ''}{trend.toFixed(1)}% from yesterday
            </span>
          )}
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className="h-80" data-testid="volume-chart-container">
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" className="stroke-gray-200 dark:stroke-gray-700" />
              <XAxis 
                dataKey="date" 
                tickFormatter={formatDate}
                className="text-xs text-gray-600 dark:text-gray-400"
              />
              <YAxis 
                tickFormatter={formatCurrency}
                className="text-xs text-gray-600 dark:text-gray-400"
              />
              <Tooltip
                contentStyle={{
                  backgroundColor: 'var(--background)',
                  border: '1px solid var(--border)',
                  borderRadius: '8px',
                }}
                labelFormatter={(date) => formatDate(date as string)}
                formatter={(value, name) => [
                  name === 'volume' ? formatCurrency(value as number) : `${value} orders`,
                  name === 'volume' ? 'Volume' : 'Orders'
                ]}
              />
              <Line 
                type="monotone" 
                dataKey="volume" 
                stroke="#8b5cf6" 
                strokeWidth={3}
                dot={{ fill: '#8b5cf6', strokeWidth: 2, r: 4 }}
                activeDot={{ r: 6 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/admin-header.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { 
  Menu, 
  LogOut, 
  User, 
  ChevronRight,
  Bell,
  Search,
  Settings
} from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import type { BreadcrumbItem } from "./admin-types";
import { useToast } from "@/hooks/use-toast";

interface AdminHeaderProps {
  title?: string;
  breadcrumbs?: BreadcrumbItem[];
  onMenuClick: () => void;
}

interface AdminUser {
  id: string;
  email: string;
  role: string;
}

export function AdminHeader({ title, breadcrumbs = [], onMenuClick }: AdminHeaderProps) {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [user, setUser] = useState<AdminUser | null>(null);
  const [notifications] = useState(3); // Mock notifications count

  useEffect(() => {
    // Get user info from localStorage or API
    const checkAuth = async () => {
      const token = localStorage.getItem('auth_token');
      if (!token) return;

      try {
        const response = await fetch('/api/auth/verify', {
          headers: { 'Authorization': `Bearer ${token}` },
        });

        if (response.ok) {
          const result = await response.json();
          setUser(result.user);
        }
      } catch (error) {
        console.error('Failed to get user info:', error);
      }
    };

    checkAuth();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('auth_token');
    toast({
      title: "Logged out",
      description: "You have been successfully logged out",
    });
    setLocation('/admin/login');
  };

  const getPageTitle = () => {
    if (title) return title;

    // Generate title from current path
    const path = window.location.pathname;
    const segments = path.split('/').filter(Boolean);
    const lastSegment = segments[segments.length - 1];

    if (lastSegment === 'admin' || !lastSegment) return 'Dashboard';
    return lastSegment.charAt(0).toUpperCase() + lastSegment.slice(1);
  };

  const getCurrentBreadcrumbs = (): BreadcrumbItem[] => {
    if (breadcrumbs.length > 0) return breadcrumbs;

    // Generate breadcrumbs from current path
    const path = window.location.pathname;
    const segments = path.split('/').filter(Boolean);

    const crumbs: BreadcrumbItem[] = [
      { label: 'Admin', href: '/admin' }
    ];

    if (segments.length > 1) {
      const section = segments[segments.length - 1];
      if (section !== 'admin') {
        crumbs.push({ 
          label: section.charAt(0).toUpperCase() + section.slice(1) 
        });
      }
    }

    return crumbs;
  };

  return (
    <header className="sticky top-0 z-10 flex h-16 items-center justify-between border-b border-gray-200 bg-white px-4 dark:border-gray-700 dark:bg-gray-800 sm:px-6 lg:px-8">
      {/* Left side */}
      <div className="flex items-center space-x-4">
        {/* Mobile menu button */}
        <Button
          variant="ghost"
          size="sm"
          onClick={onMenuClick}
          className="lg:hidden text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
          data-testid="button-mobile-menu"
        >
          <Menu className="h-5 w-5" />
        </Button>

        {/* Breadcrumbs */}
        <nav className="flex items-center space-x-2 text-sm">
          {getCurrentBreadcrumbs().map((crumb, index) => (
            <div key={index} className="flex items-center space-x-2">
              {index > 0 && <ChevronRight className="h-4 w-4 text-gray-400" />}
              {crumb.href ? (
                <a
                  href={crumb.href}
                  className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200 transition-colors"
                  data-testid={`breadcrumb-${index}`}
                >
                  {crumb.label}
                </a>
              ) : (
                <span className="font-medium text-gray-900 dark:text-white">
                  {crumb.label}
                </span>
              )}
            </div>
          ))}
        </nav>
      </div>

      {/* Page title */}
      <div className="hidden sm:block">
        <h1 className="text-xl font-semibold text-gray-900 dark:text-white">
          {getPageTitle()}
        </h1>
      </div>

      {/* Right side */}
      <div className="flex items-center space-x-4">
        {/* Search */}
        <div className="hidden md:block">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-gray-400" />
            <Input
              placeholder="Search..."
              className="w-64 pl-9 bg-gray-50 dark:bg-gray-700 border-gray-200 dark:border-gray-600"
              data-testid="input-search"
            />
          </div>
        </div>

        {/* Notifications */}
        <Button
          variant="ghost"
          size="sm"
          className="relative text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
          data-testid="button-notifications"
        >
          <Bell className="h-5 w-5" />
          {notifications > 0 && (
            <Badge 
              variant="destructive" 
              className="absolute -top-1 -right-1 h-5 w-5 rounded-full p-0 text-xs flex items-center justify-center"
            >
              {notifications}
            </Badge>
          )}
        </Button>

        {/* User menu */}
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button
              variant="ghost"
              className="flex items-center space-x-2 text-gray-700 hover:text-gray-900 dark:text-gray-300 dark:hover:text-white"
              data-testid="button-user-menu"
            >
              <div className="h-8 w-8 rounded-full bg-purple-100 dark:bg-purple-900/20 flex items-center justify-center">
                <User className="h-4 w-4 text-purple-600 dark:text-purple-400" />
              </div>
              <div className="hidden sm:block text-left">
                <div className="text-sm font-medium">
                  {user?.email || 'Admin'}
                </div>
                <div className="text-xs text-gray-500 dark:text-gray-400">
                  Administrator
                </div>
              </div>
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="w-56">
            <DropdownMenuLabel>My Account</DropdownMenuLabel>
            <DropdownMenuSeparator />
            <DropdownMenuItem data-testid="menu-item-profile">
              <User className="mr-2 h-4 w-4" />
              <span>Profile</span>
            </DropdownMenuItem>
            <DropdownMenuItem data-testid="menu-item-settings">
              <Settings className="mr-2 h-4 w-4" />
              <span>Settings</span>
            </DropdownMenuItem>
            <DropdownMenuSeparator />
            <DropdownMenuItem 
              onClick={handleLogout}
              className="text-red-600 dark:text-red-400"
              data-testid="menu-item-logout"
            >
              <LogOut className="mr-2 h-4 w-4" />
              <span>Log out</span>
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    </header>
  );
}

client/src/components/admin-layout.tsx.content:
import { ReactNode, useState } from "react";
import { AdminSidebar } from "./admin-sidebar";
import { AdminHeader } from "./admin-header";
import type { BreadcrumbItem } from "./admin-types";

interface AdminLayoutProps {
  children: ReactNode;
  title?: string;
  breadcrumbs?: BreadcrumbItem[];
  currentSection?: string;
}

export function AdminLayout({ children, title, breadcrumbs = [], currentSection = "dashboard" }: AdminLayoutProps) {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <div className="flex h-screen bg-gray-50 dark:bg-gray-900">
      {/* Mobile sidebar overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-20 bg-black bg-opacity-50 lg:hidden"
          onClick={() => setSidebarOpen(false)}
          data-testid="sidebar-overlay"
        />
      )}

      {/* Sidebar */}
      <div className={`
        fixed inset-y-0 left-0 z-30 w-64 transform transition-transform duration-300 ease-in-out lg:translate-x-0 lg:static lg:inset-0
        ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}
      `}>
        <AdminSidebar 
          currentSection={currentSection} 
          onClose={() => setSidebarOpen(false)}
        />
      </div>

      {/* Main content area */}
      <div className="flex flex-1 flex-col overflow-hidden">
        {/* Header */}
        <AdminHeader 
          title={title}
          breadcrumbs={breadcrumbs}
          onMenuClick={() => setSidebarOpen(true)}
        />

        {/* Page content */}
        <main className="flex-1 overflow-y-auto bg-gray-50 dark:bg-gray-900">
          <div className="container mx-auto px-4 py-6 sm:px-6 lg:px-8">
            {children}
          </div>
        </main>
      </div>
    </div>
  );
}

