    fromCurrency: string;
    toCurrency: string;
    fromAmount: string;
    toAmount: string;
    status: string;
    createdAt: string;
  }>;
  exchanges24h: number;
  totalVolume: number;
  volumeByDay: Array<{
    date: string;
    volume: number;
    orders: number;
  }>;
